import React, { useState } from 'react';
import { Button, Card, Dropdown } from 'react-bootstrap';
//Component
import type from '../../../data/type';

function Product() {
  const [types, setType] = useState(type[0]);
  const products = types.product;
  return (
    <div>
      <div className="container">
        <h3 className="text-center pb-3">選擇產品</h3>

        <Dropdown>
          <Dropdown.Toggle variant="dark" id="dropdown-basic">
            Dropdown Button
          </Dropdown.Toggle>
          <Dropdown.Menu>
            {type.map((item) => {
              <Dropdown.Item href="#/action-1">{item.id}</Dropdown.Item>;
            })}
          </Dropdown.Menu>
          ;
        </Dropdown>
        <div className="d-flex justify-content-center">
          {type.map((item) => (
            <div>
              <Button
                style={{ borderRadius: '50px', margin: '10px' }}
                variant="btn btn-outline-dark"
                onClick={() => {
                  setType(item);
                }}
              >
                {item.id}
              </Button>
            </div>
          ))}
        </div>
        <div className="row">
          {products.map((item) => (
            <div className="col-lg-3 text-center pb-3 pt-5">
              <Card>
                <Card.Img variant="top" src={item.image} />
                <Card.Body>
                  <Card.Title>{item.name}</Card.Title>
                  <Card.Text>
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </Card.Text>
                  <Button variant="btn btn-outline-dark btn-sm m-1">
                    <i class="fas fa-search-plus"></i>快速瀏覽
                  </Button>
                  <Button variant="btn btn-outline-dark btn-sm m-1">
                    <i class="fas fa-cart-plus"></i>加到購物車
                  </Button>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Product;
