import React, { useContext } from 'react';
import { contextApi } from '../Redux/Reducer';

function Checkout() {
  const { item } = useContext(contextApi);
  const removeItemId = item.cartItem;
  console.log(removeItemId, 'removeItemId');
  return (
    <div>
      <div className="container">
        <h3 className="text-center">購物車</h3>
        <div className="row d-flex justify-content-center">
          <div className="col-sm-8 d-flex justify-content-around" style={{border: '1px solid black'}}>
            <table className="table">
              <tbody>
                <tr>
                  <th scope="col" className="ml-5 mr-5"><h4>品名</h4></th>
                  <th scope="col" ><h4>價格</h4></th>
                  <th scope="col" ><h4>數量</h4></th>
                  <th scope="col" ><h4>刪除</h4></th>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Checkout;
