import React, { useContext } from 'react';
import { sendItemIndexKey } from '../../../Redux/Reducer';
import { Button } from 'react-bootstrap';

function Offcanvas() {
  const { itemId } = useContext(sendItemIndexKey);
  return (
    <div>
      <img
        src={itemId.image}
        class="img-fluid"
        alt=""
      />
      <div>簡單介紹</div>
      <h3>$130</h3>
      <div style={{fontSize: '10px'}}>型號</div>
      <div>查看更多資訊</div>
      <Button variant="btn btn-outline-danger btn-sm">加入購物車</Button>
      <div>庫存:</div>
    </div>
  );
}
export default Offcanvas;
