import React, { useContext } from 'react';
import { Card, Button, Modal } from 'react-bootstrap';

import { changeProductType } from '../../../Redux/Reducer';
import { home, profile, content } from '../../../data';

function MyVerticallyCenteredModal(props) {
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Modal heading
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <h4>Centered Modal</h4>
        <p>
          Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
          dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
          consectetur ac, vestibulum at eros.
        </p>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}

function ProductCarousel() {
  const { products } = useContext(changeProductType);
  const [modalShow, setModalShow] = React.useState(false);
  if (products === 'home') {
    return (
      <div className="container">
        <div className="row text-center">
          {home.map((item, key) => (
            <div className="col-lg-3 pt-5">
              <Card style={{ width: '18rem' }}>
                <Card.Img
                  variant="top"
                  src="https://images.unsplash.com/photo-1586942593822-268ff3828458?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1050&q=80"
                />
                <Card.Body>
                  <Card.Title>{item.id}</Card.Title>
                  <Card.Text>
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </Card.Text>
                  <Button variant="primary" onClick={() => setModalShow(true)}>
                    Go somewhere
                  </Button>
                  <MyVerticallyCenteredModal
                    show={modalShow}
                    onHide={() => setModalShow(false)}
                  />
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </div>
    );
  } else if (products === 'profile') {
    return (
      <div className="container">
        <div className="row">
          {profile.map((item, key) => (
            <div className="col-lg-3 pt-5">
              <Card style={{ width: '18rem' }}>
                <Card.Img variant="top" src="holder.js/100px180" />
                <Card.Body>
                  <Card.Title>{item.id}</Card.Title>
                  <Card.Text>
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </Card.Text>
                  <Button variant="primary">Go somewhere</Button>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </div>
    );
  } else {
    return (
      <div className="container">
        <div className="row">
          {content.map((item, key) => (
            <div className="col-lg-3 pt-5">
              <Card style={{ width: '18rem' }}>
                <Card.Img variant="top" src="holder.js/100px180" />
                <Card.Body>
                  <Card.Title>{item.id}</Card.Title>
                  <Card.Text>
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </Card.Text>
                  <Button variant="primary">Go somewhere</Button>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default ProductCarousel;
