import React, { useEffect, useState } from 'react';
import { Button, Card } from 'react-bootstrap';
import Test from './test';
//Component
import type from '../../../data/type';

function Product() {
  const [types, setType] = useState(type[0].id);
  const products = types.product;
  console.log(types, 'types');
  console.log(types.id)
//   console.log(products, 'pr');
//   console.log(types.product, 'types.product')
  return (
    <div>
      <div className="container">
        <h3 className="text-center pb-3">產品</h3>
        <div className="d-flex justify-content-center">
          {type.map((item) => (
            <div>
              <Button
                style={{ borderRadius: '50px', margin: '10px' }}
                variant="btn btn-outline-dark"
                onClick={() => {
                  setType(item);
                }}
              >
                {item.id}
              </Button>
            </div>
          ))}
        </div>
        {/* {products.map((item)=>(
            <h1>{item.id}</h1>
        ))} */}
        <h1>{types}</h1>
        {/* <Test products={products}/> */}
        <Card style={{ width: '18rem' }}>
          <Card.Img variant="top" src="holder.js/100px180" />
          <Card.Body>
            <Card.Title>{types.id}</Card.Title>
            <Card.Text>
              Some quick example text to build on the card title and make up the
              bulk of the card's content.
            </Card.Text>
            <Button variant="primary">Go somewhere</Button>
          </Card.Body>
        </Card>
      </div>
    </div>
  );
}

export default Product;
