import React, { useContext } from 'react';
import { sendItemIndexKey } from '../../../Redux/Reducer';
import { Button } from 'react-bootstrap';

function Offcanvas() {
  const { itemId } = useContext(sendItemIndexKey);
  return (
    <div>
      <img src={itemId.image} class="img-fluid" alt="" />
      <h1 className="pt-3">{itemId.name}</h1>
      <div>{itemId.info}</div>
      <h3 className="pb-3 pt-3"><del>${itemId.price}</del></h3>
      <div>庫存:{itemId.stock}</div>
      <div style={{ fontSize: '15px' }}>型號:{itemId.modal}</div>
      <div className="text-center">
        <Button className="mt-5 m-2" variant="btn btn-outline-primary btn-sm">
          查看更多資訊
        </Button>
        <Button className="mt-5 m-2" variant="btn btn-outline-danger btn-sm">
          加入購物車
        </Button>
      </div>
    </div>
  );
}
export default Offcanvas;
