import React, { useState, useContext } from 'react';
import { Button, Card, Dropdown } from 'react-bootstrap';
//Component
import type from '../../../data/type';
import Offcanvas from './Offcanvas';
import { sendItemIndexKey } from '../../../Redux/Reducer';
import { SEND_ITEM_INDEX } from '../../../Redux/Action/actionType';

function Product() {
  const [types, setType] = useState(type[0]);
  const products = types.product;
  const { dispatch } = useContext(sendItemIndexKey);
  return (
    <div>
      <div className="container">
        <h3 className="text-center pb-3 smFade">選擇產品</h3>
        <div className="text-center smShow">
          <Dropdown>
            <Dropdown.Toggle variant="btn btn-outline-dark" id="dropdown-basic">
              選擇產品
            </Dropdown.Toggle>

            <Dropdown.Menu>
              {type.map((item, key) => (
                <Dropdown.Item onClick={() => setType(item, key)}>
                  {item.id}
                </Dropdown.Item>
              ))}
            </Dropdown.Menu>
          </Dropdown>
        </div>

        <div className="d-flex justify-content-center smFade">
          {type.map((item) => (
            <div>
              <Button
                style={{ borderRadius: '50px', margin: '10px' }}
                variant="btn btn-outline-dark"
                onClick={() => {
                  setType(item);
                }}
              >
                {item.id}
              </Button>
            </div>
          ))}
        </div>
        <div className="row">
          {products.map((item, key) => (
            <div className="col-lg-3 text-center pb-3 pt-5">
              <Card style={{ borderRadius: '20px' }}>
                <Card.Img
                  variant="top"
                  src={item.image}
                  style={{
                    borderTopLeftRadius: '20px',
                    borderTopRightRadius: '20px',
                  }}
                />
                <Card.Body>
                  <Card.Title>{item.name}</Card.Title>
                  <Card.Text>
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </Card.Text>
                  {/* <Button
                    variant="btn btn-outline-dark btn-sm m-1"
                  >
                    <i class="fas fa-search-plus"></i>快速瀏覽
                  </Button> */}
                  <button
                    class="btn btn-outline-dark btn-sm m-1"
                    type="button"
                    data-bs-toggle="offcanvas"
                    data-bs-target="#offcanvasRight"
                    aria-controls="offcanvasRight"
                    onClick={() =>
                      dispatch({
                        type: SEND_ITEM_INDEX,
                        itemId: types.product[key],
                      })
                    }
                  >
                    <i class="fas fa-search-plus"></i>快速瀏覽
                  </button>
                  <div
                    class="offcanvas offcanvas-end"
                    tabIndex="-1"
                    id="offcanvasRight"
                    aria-labelledby="offcanvasRightLabel"
                  >
                    {/* <Test id = {types.product}/> */}
                    <div class="offcanvas-header">
                      <button
                        type="button"
                        class="btn-close text-reset"
                        data-bs-dismiss="offcanvas"
                        aria-label="Close"
                      ></button>
                    </div>
                    <div class="offcanvas-body">
                      <Offcanvas id={types.product} />
                    </div>
                  </div>
                  <Button variant="btn btn-outline-dark btn-sm m-1">
                    <i class="fas fa-cart-plus"></i>加到購物車
                  </Button>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Product;
