import React, { useState } from 'react';
import { Button, Card, Modal, Dropdown } from 'react-bootstrap';
//Component
import type from '../../../data/type';

function MyVerticallyCenteredModal(props) {
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      {/* <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Modal heading
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <h4>Centered Modal</h4>
        <p>
          Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
          dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
          consectetur ac, vestibulum at eros.
        </p>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer> */}
      <div className="col-sm-6">
        <img src="https://images.unsplash.com/photo-1584634731339-252c581abfc5?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1054&q=80" style={{maxHeight: '500px'}} alt="" />
      </div>
      <div className="col-sm-6">
        456
      </div>
    </Modal>
  );
}

function Product() {
  const [types, setType] = useState(type[0]);
  const products = types.product;
  const [modalShow, setModalShow] = React.useState(false);
  return (
    <div>
      <div className="container">
        <h3 className="text-center pb-3 smFade">選擇產品</h3>
        <div className="text-center smShow">
          <Dropdown>
            <Dropdown.Toggle variant="btn btn-outline-dark" id="dropdown-basic">
              選擇產品
            </Dropdown.Toggle>

            <Dropdown.Menu>
              {type.map((item) => (
                <Dropdown.Item onClick={() => setType(item)}>
                  {item.id}
                </Dropdown.Item>
              ))}
            </Dropdown.Menu>
          </Dropdown>
        </div>

        <div className="d-flex justify-content-center smFade">
          {type.map((item) => (
            <div>
              <Button
                style={{ borderRadius: '50px', margin: '10px' }}
                variant="btn btn-outline-dark"
                onClick={() => {
                  setType(item);
                }}
              >
                {item.id}
              </Button>
            </div>
          ))}
        </div>
        <div className="row">
          {products.map((item) => (
            <div className="col-lg-3 text-center pb-3 pt-5">
              <Card style={{ borderRadius: '20px' }}>
                <Card.Img
                  variant="top"
                  src={item.image}
                  style={{
                    borderTopLeftRadius: '20px',
                    borderTopRightRadius: '20px',
                  }}
                />
                <Card.Body>
                  <Card.Title>{item.name}</Card.Title>
                  <Card.Text>
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </Card.Text>
                  <Button
                    variant="btn btn-outline-dark btn-sm m-1"
                    onClick={() => setModalShow(true)}
                  >
                    <i class="fas fa-search-plus"></i>快速瀏覽
                  </Button>
                  <MyVerticallyCenteredModal
                    show={modalShow}
                    onHide={() => setModalShow(false)}
                  />
                  <Button variant="btn btn-outline-dark btn-sm m-1">
                    <i class="fas fa-cart-plus"></i>加到購物車
                  </Button>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Product;
