import React, { useReducer } from 'react';
import { CHANGE_PRODUCT } from '../Action/actionType';

//Component
import Recommend from '../../Component/Front/MainPage/Recommend';

const initialState = {
  productType: '',
};
export const reducer = (state = initialState, action) => {
  switch (action.type) {
    case CHANGE_PRODUCT:
      return action.products;
    default:
      return state;
  }
};

export const changeProductType = React.createContext({});

export const ProductType = props => {
