import React, { useContext } from 'react';
import { contextApi } from '../Redux/Reducer';

function Checkout() {
  const { item } = useContext(contextApi);
  const removeItemId = item.cartItem;
  console.log(removeItemId, 'removeItemId');
  return (
    <div>
      <div className="container">
        <h3 className="text-center">購物車</h3>
        <div className="row d-flex justify-content-center">
          <div
            className="col-sm-8 d-flex justify-content-around text-center"
            style={{ border: '1px solid black' }}
          >
            <table className="table">
              <thead>
                <tr>
                  <th scope="col">
                    <h4>品名</h4>
                  </th>
                  <th scope="col">
                    <h4>價格</h4>
                  </th>
                  <th scope="col">
                    <h4>數量</h4>
                  </th>
                  <th scope="col">
                    <h4>刪除</h4>
                  </th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  {removeItemId.map((item, key) => {
                    <div>
                      <td scope="col">{item.name}</td>
                      <td scope="col">${item.price}</td>
                      <td scope="col">1個</td>
                      {console.log(item.name, 'inside item')}
                    </div>;
                  })}
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Checkout;
