import React, { useContext } from 'react';
import { Navbar, Nav, Container, Dropdown } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { addItemToCart } from '../Redux/Reducer';

function NavBar() {
  const { item } = useContext(addItemToCart);
  console.log(item, 'item');
  return (
    <div>
      <Navbar bg="light" expand="lg">
        <Container>
          <Navbar.Brand>
            <Link to="/" className="mainLink">
              ＨＣＡＲＥ
            </Link>
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Link className="NavLink" to="/product">
                產品
              </Link>
              <Link className="NavLink" to="/brand">
                品牌介紹
              </Link>
            </Nav>
            <div className="smFade d-flex">
              <i className="fas fa-user fa-2x"></i>
              {/* <Link to="/carts"> */}
              {/* <i className="fas fa-shopping-cart shopCartIcon fa-2x" />
              <span className="numberIcon">{item.length}</span> */}
              {/* </Link> */}
              <div class="dropdown">
                <button
                  class=" dropdown-toggle"
                  // type="button"
                  id="dropdownMenuButton1"
                  data-bs-toggle="dropdown"
                  aria-expanded="false"
                  style={{ border: '0px', backgroundColor: 'rgba(0,0,0,0)' }}
                >
                  <i className="fas fa-shopping-cart shopCartIcon fa-2x" />
                  <span className="numberIcon">{item.length}</span>
                </button>
                <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                  <h6 className="text-center">已選擇商品</h6>
                  {item.map((item) => {
                    <li>
                      <a class="dropdown-item" href="#">
                        {item.name}
                      </a>
                    </li>;
                  })}
                </ul>
              </div>
            </div>
            <div className="smShow pb-2 pt-2">
              <Link className="NavLink" to="/brand">
                顧客資訊
              </Link>
            </div>
            <div className="smShow pt-2">
              <Link className="NavLink" to="/brand">
                購物車
              </Link>
            </div>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </div>
  );
}

export default NavBar;
